<?php/*** @copyright	Copyright (C) 2008 - 2009  All rights reserved.* @license*/// no direct accessdefined('_JEXEC') or die('Restricted access');function pagination_list_footer($list){$html = "<div class=\"list-footer\">\n";$html .= "\n<div class=\"limit\">".JText::_('Display Num').$list['limitfield']."</div>";$html .= $list['pageslinks'];$html .= "\n<div class=\"counter\">".$list['pagescounter']."</div>";$html .= "\n<input type=\"hidden\" name=\"limitstart\" value=\"".$list['limitstart']."\" />";$html .= "\n</div>";return $html;}function pagination_list_render($list){// Initialize variables$html = '<ul class="pagination">';$html .= $list['start']['data'];$html .= $list['previous']['data'];foreach( $list['pages'] as $page ){$html .= $page['data'];}$html .= $list['next']['data'];$html .= $list['end']['data'];$html .= '</ul>';return $html;}function pagination_item_active(&$item) {return '<li class="'.(isset($item->active) && ($item->active) ? 'active' : '').'"><a href="'.$item->link.'" title="'.$item->text.'">'.$item->text.'</a></li>';}function pagination_item_inactive(&$item) {return '<li class="disabled"><a href="#">'.$item->text.'</a></li>';}?>